import React from "react";
import "./HeroSection.css";
function HeroSection({ heading1, heading2, img, imgStart, bgDark }) {
  return (
    <>
      <div className="first-hero-section">
        <div
          className={
            bgDark ? "hero-container bg-dark" : "hero-container bg-dark"
          }
          style={{
            flexDirection: imgStart ? "row-reverse" : "row",
          }}
        >
          <div className="text-wrapper">
            <div className="text-content">
              <h1>{heading1}</h1>
              <h1>{heading2}</h1>
            </div>
          </div>
          <div className="image-wrapper">
            <div className="image-content">
              <img src={img} alt="hello"></img>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default HeroSection;
