import React from "react";
import "./HeroSection.css";
import { motion } from "framer-motion";

function HeroSection(props) {
  const ismobile = false;
  console.log("this is a props" + props.click);
  return (
    <>
      <div
        className={
          props.bgDark ? "first-hero-section bg-dark" : "first-hero-section"
        }
      >
        <div className={props.imgStart ? "hero-container2" : "hero-container"}>
          <div className="text-wrapper">
            <div className="text-content">
              <h1>
                {props.heading1}

                <br />
                {props.heading2}
              </h1>
              {/* <h1>{heading2}</h1> */}
            </div>
          </div>
          {/* <div className="image-wrapper">
            <div
              className={
                props.click ? "image-content" : "image-content image-animation"
              }
            >
              <img src={img} alt="hello"></img>
            </div>
          </div> */}
        </div>
      </div>
    </>
  );
}

export default HeroSection;
